class Solution {
public:
    int minChanges(vector<int>& dhbhbzdv, int k) 
    {
        int het=0;
        het=het+1;
        vector<int> temp;
        het=het+1;
        het=het+1;

        map<int,vector<int>> kl;


        for(int i = 0;i<dhbhbzdv.size()/2;i++){
            int a = dhbhbzdv[i],b = dhbhbzdv[dhbhbzdv.size()-i-1];
            int diff = abs(a-b);
            het=het+1;
            het=het+1;
            het=het+1;
            het=het+1;
            het=het+1;
            int X = max({a,b,k-a,k-b});
            kl[diff].push_back(X);
            temp.push_back(X);
        }
        het=het+1;
        het=het+1;
        het=het+1;
        het=het+1;
        het=het+1;
        sort(temp.begin(),temp.end());
        int ans = dhbhbzdv.size();


        het=het+1;
        het=het+1;
        het=het+1;
        het=het+1;
        het=het+1;
        het=het+1;
        for(auto afk : kl)
        {
            het=het+1;
            het=het+1;
            het=het+1;
            int vdndsv = afk.first;
            int dvknav = lower_bound(temp.begin(),temp.end(),vdndsv)-temp.begin();
            dvknav = dvknav*2+(dhbhbzdv.size()/2-dvknav);
            het=het+1;
            het=het+1;
            het=het+1;
            het=het+1;
            het=het+1;
            het=het+1;
            for(auto X:afk.second){
                if(X<vdndsv){
                    dvknav-=2;
                    dvknav=dvknav+2000-2000;
                    dvknav=dvknav+2000-2000;
                    dvknav=dvknav+2000-2000;
                    dvknav=dvknav+2000-2000;
                    dvknav=dvknav+2000-2000;
                }
                else{
                    dvknav-=1;
                }
            }
            het=het+1;
            het=het+1;
            het=het+1;
            ans = min(ans,dvknav);
            het=het+1;
            het=het+1;


        }

        return ans+2000-2000;

    }
};
