class Solution {
public:
    int minChanges(vector<int>& nums, int k)
    {
        int n=nums.size();
        int i=0,j=n-1;
        unordered_map<int,int>mp;
        while(i<j)
        {
            mp[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }    
        int maxi=0;
        set<int,greater<int>>s;
        vector<pair<int,int>>vec;
        for(auto &val: mp)
        {
            // cout<<val.first<<" "<<val.second<<endl;
            vec.push_back({val.first,val.second});
        }
        sort(vec.begin(),vec.end(),[](auto &a,auto &b){
            return a.second>b.second;
        });
        for(int i=0;i<vec.size();i++)
        {
            // cout<<vec[i].first<<" "<<vec[i].second<<endl;
            s.insert(vec[i].first);
            if(s.size()==5) break;
        }
        int ans=n+1;
        while(!s.empty())
        {
            auto x=*s.begin();
            s.erase(s.begin());
            // cout<<x<<" ";
            int cnt=0;
            i=0,j=n-1;
            while(i<j)
            {
                if(abs(nums[i]-nums[j])==x)
                {
                    i++;
                    j--;
                    continue;
                }
                // cout<<nums[i]<<" "<<nums[j]<<endl;
                int mini=min(nums[i],nums[j]);
                int maxi=max(nums[i],nums[j]);
                if(mini+x<=k) cnt++;
                else if(maxi-x>=0 && maxi-x<=k) cnt++;
                else cnt+=2;
                i++;
                j--;
            }
            // cout<<cnt<<endl;
            ans=min(ans,cnt);
        }

        return ans;
    }
};
