class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),req=n;
        vector<int>res;
        priority_queue<int>pq;
        map<int,vector<int>>mp;
        for(int i=0;i<n/2;i++){
            pq.push(nums[i]);
            int v=abs(nums[i]-nums[n-i-1]);
            mp[v].push_back(max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]}));
            res.push_back(max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]}));
        }

        int x=1;
        while(pq.size()>1)x=pq.top(),pq.pop();
        sort(res.begin(),res.end());
        for(auto it:mp){
            int a=lower_bound(res.begin(),res.end(),it.first)-res.begin();
            a=a*2+(n/2-a);
            for(auto z:it.second){
                if(z<it.first && pq.size())a-=1;
                    else a-=1;
            }
            req=min(req,a);            
        }
        return req;
    }
};
