class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> mp;
        vector<int> store;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

            int X = max({a, b, k - a, k - b});

            mp[diff].push_back(X);
            store.push_back(X);
        }

        int result = nums.size();  
        sort(store.begin(), store.end());

        for (const auto& [diff, values] : mp) {
            int T = lower_bound(store.begin(), store.end(), diff) - store.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (auto& X : values)
                X < diff ? T -= 2 : T -= 1;

            result = min(result, T);
        }

        return result;
    }
};
