class Solution {
public:
    int calculate(vector<int> &nums,int k,int req_diff){
        int n=nums.size()/2;
        int count=0;
        for(int i=0;i<n;i++){
                if(abs(nums[i]-nums[2*n-i-1])==req_diff) continue;
                else{
                    int ab=abs(nums[i]-nums[2*n-i-1]);
                    if(nums[i]+req_diff<=k){
                        count++;
                    }
                    else if(nums[2*n-i-1]+req_diff<=k){
                        count++;
                    }
                    else if(nums[i]-req_diff>=0){
                        count++;
                    }
                    else if(nums[2*n-i-1]-req_diff>=0){
                        count++;
                    }
                    else count+=2;
                }
            }
        return count;
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size()/2;
        map<int,int> diff;
        for(int i=0;i<n;i++){
            diff[abs(nums[i]-nums[2*n-i-1])]++;
        }
        if(diff.size()==1) return 0;
        else{
            priority_queue<pair<int,int>> pq;
            for(auto it:diff){
                pq.push({it.second,it.first});
            }
            int ans=2*n;
            while(!pq.empty()){
                auto it=pq.top();
                int count=calculate(nums,k,it.second);
                if(ans>=count) ans=count;
                else break;
                pq.pop();

            }
            return ans;
        }
    }
};
