class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int c1 = nums.size() , c2 = nums.size();
        long long c3 = 0 , c4 = 0;
        vector<int>v1;
        vector<long long>v2;
        unordered_map<int , vector<int>>m1;
        unordered_map<char , int>m2;
        for(int i = 0; i<c1/2 ;i++){
            c3 = nums[i];
            c4 = nums[c1-i-1];
            int maxi = max({c3 , c4 , k-c3 , k-c4});
            m1[abs(c3 - c4)].push_back(maxi);
            v1.push_back(maxi);
        }
        sort(v1.begin() , v1.end());
        for(auto & i : m1){
            int index = lower_bound(v1.begin() , v1.end() ,i.first) - v1.begin();
            index = index *2 + (c1/2- index);
            for(auto & j : i.second){
                if(j < i.first){
                    index -= 2;
                }
                else{
                    index -= 1;
                }
            }
            c2 = min(c2 , index);
        }

        return c2;
    }
};
