class Solution {
    public int minChanges(int[] nums, int k) {
           Map<Integer, List<Integer>> map = new HashMap<>();
        List<Integer> list = new ArrayList<>();
        int n = nums.length;

        for (int i = 0; i < n / 2; i++) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = Math.abs(a - b);
            int X = Math.max(Math.max(a, b), Math.max(k - a, k - b));
            map.computeIfAbsent(diff, x -> new ArrayList<>()).add(X);
            list.add(X);
        }

        int ans = n;
        Collections.sort(list);

        for (Map.Entry<Integer, List<Integer>> entry : map.entrySet()) {
            int diff = entry.getKey();
            int val = binarySearch(list, diff) * 2 + (n / 2 - binarySearch(list, diff));

            for (int num : entry.getValue()) {
                if (num < diff) {
                    val -= 2;
                } else {
                    val -= 1;
                }
            }

            ans = Math.min(ans, val);
        }

        return ans;
    }

    private int binarySearch(List<Integer> list, int key) {
        int lo = 0, hi = list.size();
        while (lo < hi) {
            int mid = (lo + hi) / 2;
            if (list.get(mid) < key) {
                lo = mid + 1;
            } else {
                hi = mid;
            }
        }
        return lo;

    }
}
