class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int res = n;

        map<int,int>mp;
        vector<int>cnt(k+1,0);

        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-1-i])]++;

            int a1 = abs(k-nums[i]);
            int a2 = abs(k -nums[n-1-i]);
            int temp = max({a1,a2,nums[i],nums[n-1-i]});
            cnt[temp]++;
        }

        for(int i=k-1;i>=0;i--){
            cnt[i] += cnt[i+1];
        }

        for(int i=0;i<=k;i++){

            int temp = n - cnt[i] - mp[i];
            // cout<<temp<<" "<<i<<" "<<cnt[i]<<" "<<mp[i]<<endl;
            res = min(res,temp);
        }


        return res;

    }
};
