class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int diff[k+1];
        int atMax[k+1];
        for(int i = 0; i<=k; i++){
            diff[i] = 0;
            atMax[i] = 0;
        }

        int n = nums.size();
        int ans = n;
        int l = 0;
        int r = n-1;
        while(l < r){
            int x = abs(nums[r] - nums[l]);
            diff[x]++;


            int y = max(nums[r], nums[l]);
            y = max(k - nums[l], y);
            y = max(k - nums[r], y);
            atMax[y]++;

            l++;
            r--;
        }

        int cum = 0;
        for(int i = k; i>=0; i--){
          cum += atMax[i];
          int req = n - diff[i] - cum;
          ans = min(ans, req);
        }
        return ans;
    }
};
