class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        vector<int> A(k + 1, 0);
        vector<int> ct(k + 1, 0);

        for (int x = 0; x < n / 2; ++x){
            int a = nums[x];
            int b = nums[n - 1 - x];
            if (a > b) swap(a, b);
            ct[b - a] += 1;
            int one = max(k - a, b);
            A[one] += 1;
        }

        int ret = n;
        int by_one = accumulate(A.begin(), A.end(), 0);
        for (int x = 0; x <= k; x++){
            ret = min(ret, by_one - ct[x] + 2 * (n / 2 - by_one));
            by_one -= A[x];
        }
        return ret;
    }
};
