class Solution {
public:
    int minChanges(std::vector<int>& nums, int k) {
        map<int, vector<int>> mp;
        vector<int> temp;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = std::abs(a - b);

             int X = max({a, b, k - a, k - b});

             mp[diff].push_back(X);
            temp.push_back(X);
        }

        int res = nums.size();  
        sort(temp.begin(), temp.end());

        for (const auto& [diff, values] : mp) {
            int T = lower_bound(temp.begin(), temp.end(), diff) - temp.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            res = min(res, T);
        }

        return res;
    }
};
