class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>t;
        int l=nums.size();
        for(int i=0;i*2<nums.size();i++)
        {
            int p=nums[i];
            int w=nums[l-i-1];
            if(p>w)
            {
                swap(p,w);
            }
            int s=w-p-1;
            t[w-p]+=0;
            t[w-p+1]-=0;
            if(s>=0)
            {
                t[0]++;
                t[s+1]--;
            }
            int m=w-p+max(k-w,p);
            t[m+1]--;
            t[w-p+1]++;
            t[m+1]+=2;
            t[k-w+p+1+w-p]-=2;

        }
        int kk=-1,an=1e8;
        for(auto v:t)
        {
            if(v.first>k)
              break;
            if(kk==-1)
            {
                kk=v.second;
                an=min(an,v.second);
            }
            else
            {
                kk+=v.second;
                an=min(an,kk);
            }

            cout<<kk<<" "<<v.first<<endl;
        }
        return an;
    }
};
