class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>pSum(k+1),pSum0(k+1);
        for(int i=0;i<n/2;i++){
            int mxVal=k-nums[i];
            mxVal=max(mxVal,k-nums[n-i-1]);
            mxVal=max(mxVal,nums[i]);
            mxVal=max(mxVal,nums[n-i-1]);
            pSum[mxVal]++;
            pSum0[abs(nums[i]-nums[n-i-1])]++;
        }
        int ans=n;
        for(int i=k-1;i>=0;i--){
            pSum[i]+=pSum[i+1];
        }
        for(int i=0;i<=k;i++){
            ans=min(ans,(n-pSum[i]-pSum0[i]));
        }
        return ans;
    }
};
