class Solution {
public:
    array<int, 100001> changes0, changes2;

    int calculateMaxDiff(int k, int a, int b)
    {
        int option1 = k - min(a, b);
        int option2 = max(a, b);
        return max(option1, option2);
    }

    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        for (int i = 0; i < n / 2; ++i)
        {
            int diff = abs(nums[i] - nums[n - i - 1]);
            int maxDiff = calculateMaxDiff(k, nums[i], nums[n - i - 1]);
            changes0[diff]++;
            changes2[maxDiff + 1]++;
        }

        int bestScore = numeric_limits<int>::max();
        for (int i = 0; i <= k; ++i)
        {
            if (i > 0)
                changes2[i] += changes2[i - 1];

            int score = n / 2 + changes2[i] - changes0[i];
            bestScore = min(score, bestScore);
        }

        return bestScore;
    }
};
