class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>freq;
        vector<int>arr;

        int n = nums.size();

        for(int i=0;i<n/2;i++){
            int a = nums[i];
            int b = nums[n-i-1];

            int diff = abs(a-b);

            int temp = max({a,b,k-a,k-b});

            freq[diff].push_back(temp);
            arr.push_back(temp);
        }

        int result = n;
        sort(arr.begin(),arr.end());

        for(auto entry:freq){
            int diff = entry.first;
            int store = lower_bound(arr.begin(),arr.end(),diff) - arr.begin();
            store = store * 2 + (n/2-store);

            for(auto val : entry.second){
                if(val < diff){
                    store -= 2;
                }
                else{
                    store -= 1;
                }
            }

            result = min(result,store);
        }
        return result;
    }
};
