class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int i,j,n,ans,x,y,sum,ma;
        vector<int> d=vector<int>(k+2,0);
        n=a.size();
        for (i=0;n-1-i>i;i++)
        {
            x=a[i];
            y=a[n-1-i];
            if (x>y) swap(x,y);
            d[0]++;
            d[y-x]--;
            d[y-x+1]++;
            ma=x;
            ma=max(ma,k-x);
            ma=max(ma,y);
            ma=max(ma,k-y);
            d[ma+1]++;
        }
        ans=(1<<30);
        sum=0;
        for (i=0;i<=k;i++)
        {
            sum+=d[i];
            ans=min(ans,sum);
        }
        return ans;
    }
};
