class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:

        n = len(nums)
        min_ans=inf

        dif = []
        cnt=defaultdict(int)
        for i in range(n // 2):
            dif.append(abs(nums[n - i - 1] - nums[i]))
            cnt[abs(nums[n - i - 1] - nums[i])]+=1

        sorted_dict = dict(sorted(cnt.items(), key=lambda item: item[1],reverse=True))

        for X in list(sorted_dict.keys())[0:2]:
            ans=0
            for i in range(n//2):
                if abs(nums[n - i - 1] - nums[i])!=X:
                    small=min(nums[i],nums[n - i - 1])
                    big=max(nums[i],nums[n - i - 1])
                    if small+X>k and big-X<0:
                        ans+=2
                    else:
                        ans+=1

            min_ans=min(min_ans,ans)

        return min_ans
