class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> m;
        vector<int> vec;
        int  n = nums.size();

        for (int i = 0; i < n / 2; ++i) {
            int a = nums[i];
            int b = nums[n- i - 1];
            int d = abs(a - b);

            int X = max({a, b, k - a, k - b});

            m[d].push_back(X);
            vec.push_back(X);
        }

        int ans = nums.size();
        sort(vec.begin(), vec.end());

        for (const auto& [diff, values] : m) {
            int T = lower_bound(vec.begin(), vec.end(), diff) - vec.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (auto X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = min(ans, T);
        }

        return ans;
    }
};
