class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
        map<int,vector<int>> mp;
        vector<int> v;
        for(int i = 0;i<arr.size()/2;i++){
            int a = arr[i],b = arr[arr.size()-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            mp[diff].push_back(X);
            v.push_back(X);
        }
        int ans = arr.size();
        sort(v.begin(),v.end());

        for(auto val:mp){

            int diff = val.first;
            int t = lower_bound(v.begin(),v.end(),diff)-v.begin();
            t = t*2+(arr.size()/2-t);

            for(auto &X:val.second){
                if(X<diff){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            ans = min(ans,t);

        }

        return ans;
    }
};
