class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp;
        int n = nums.size();
        int pre[k+1];
        memset(pre,0,sizeof pre);
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
            int mx = max(abs(nums[i]-k),abs(nums[n-i-1]-k));
            mx= max(mx,nums[i]);
            mx=max(mx,nums[n-i-1]);
            pre[mx]++;

        }
        for(int i=1;i<=k;i++){
            pre[i]+=pre[i-1];
        }
        int ret = n/2;
        for(auto x:mp){
            ret = min(ret,n/2-x.second+(x.first>=1?pre[x.first - 1]:0));
        }
        return ret;


    }
};
