class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        map<int,vector<int>> anku;
        vector<int> don;
        int i=0;
       while(i<nums.size()/2){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int x = max({a,b,k-a,k-b});
            anku[diff].push_back(x);
            don.push_back(x);
           i++;
        }
        int ans = nums.size();
        sort(don.begin(),don.end());

        for(auto it:anku){

            int diff = it.first;
            int t = lower_bound(don.begin(),don.end(),diff)-don.begin();
            t = t*2+(nums.size()/2-t);

            for(auto X:it.second){
                if(X<diff){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            ans = min(ans,t);

        }

        return ans;

    }
};
