class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,int>mp;

        int i = 0;
        int j = n-1;

        while(i<j){
            int diff = abs(nums[i++] - nums[j--]);
            mp[diff]++;
        }

        unordered_map<int,int>freq;

        for(auto it:mp){
            if(!freq.count(it.second)){
                freq[it.second] = it.first;
            }
            else{
                freq[it.second] = min(freq[it.second],it.first);
            }
        }

        int ans = INT_MAX;
        for(auto it:freq){
            i = 0;
            j = n-1;
            int maxDiff = it.second;
            int count = 0;

            while(i<j){
                int diff = abs(nums[i]-nums[j]);

                if(diff != maxDiff){
                    int d = abs(maxDiff - diff);
                    bool isOk = false;
                    for(int it = 0 ; it < 4 ; it++){
                        if(it < 2){
                            int num = nums[i];
                            if(it == 0){
                                num += d;
                            }
                            else{
                                num -= d;
                            }

                            if(num >= 0 && num <= k && abs(num-nums[j]) == maxDiff){
                                isOk = true;
                                break;
                            }
                        }
                        else{
                            int num = nums[j];
                            if(it == 2){
                                num += d;
                            }
                            else{
                                num -= d;
                            }

                            if(num >= 0 && num <= k && abs(nums[i] - num) == maxDiff){
                                isOk = true;
                                break;
                            }
                        }
                    }
                    count++;
                    if(!isOk) count++;
                }
                i++;
                j--;
            }
            ans = min(ans,count);
        }


        return ans;
    }
};
