class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> diff;
        vector<int> d;
        int n=nums.size();
        int ans=n;
        for(int i=0;i<n/2;i++){
            int x=abs(nums[i]-nums[n-1-i]);
            int mx_p=max(max(nums[i],nums[n-1-i]),max(k-nums[i],k-nums[n-1-i]));
            diff[x].push_back(mx_p);
            d.push_back(mx_p);
        }
        sort(d.begin(),d.end());

        for(auto it: diff){
            int a=it.first;
            vector<int> b=it.second;

            int pos= lower_bound(d.begin(), d.end(), a) - d.begin();
            int x= (pos*2) + (n/2 - pos);

            for(int i: b){
                if(i<a) x-=2;
                else x--;
            }
            ans=min(ans,x);
        }
        return ans;
    }
};
