class Solution {
    public int minChanges(int[] nums, int k) {
        Map<Integer, Integer> map = new HashMap();
        int N = nums.length;

        for(int i = 0; i < N/2; i++) {
            int diff = Math.abs(nums[i] - nums[N - i - 1]);
            map.put(diff, map.getOrDefault(diff, 0) + 1);
        }
        System.out.println(map);
        int mostFreq = 0, mostFreqDiff = 0; int mostFreqDiff2 = 0, mostFreq2 = 0;
        for(int key : map.keySet()) {
            if(map.get(key) > mostFreq || map.get(key) == mostFreq && key < mostFreqDiff) {
                mostFreqDiff2 = mostFreqDiff;
                mostFreq2 = mostFreq;
                mostFreqDiff = key;
                mostFreq = map.get(key);
            }
            else if(map.get(key) > mostFreq2 || map.get(key) == mostFreq2 && key < mostFreqDiff2) mostFreqDiff2 = key;
        }
        System.out.println("mostFreqDiff " + mostFreqDiff);
        System.out.println("mostFreqDiff2 " + mostFreqDiff2);
        int res = 0;
        for(int i = 0; i < N/2; i++) {
            int diff = Math.abs(nums[i] - nums[N - i - 1]);
            if(diff == mostFreqDiff) continue;
            int s = Math.min(nums[i], nums[N - i - 1]);
            int b = Math.max(nums[i], nums[N - i - 1]);
            if(s + mostFreqDiff > k && b - mostFreqDiff < 0) res += 2;
            else res++;
            //System.out.println("" + nums[i] + " and " + nums[N - i - 1] + "; diff  " + diff + "; res " + res);            
        }
        int res1 = 0;
        for(int i = 0; i < N/2; i++) {
            int diff = Math.abs(nums[i] - nums[N - i - 1]);
            if(diff == mostFreqDiff2) continue;
            int s = Math.min(nums[i], nums[N - i - 1]);
            int b = Math.max(nums[i], nums[N - i - 1]);
            if(s + mostFreqDiff2 > k && b - mostFreqDiff2 < 0) res1 += 2;
            else res1++;
            //System.out.println("" + nums[i] + " and " + nums[N - i - 1] + "; diff  " + diff + "; res1 " + res1);            
        }

        return Math.min(res, res1);
    }
}
