class Solution {
public:
    int minChanges(vector<int>& nums, int m) {
        int n = nums.size();
        map<int, int> mp;
        vector<int> dp(m + 1, 0);
        for(int k=0;k<n/2;k++) {
            int x = nums[k], y = nums[n - k - 1];
            mp[abs(x - y)]++;
            int c = max(x, m - x);
            c = max(y, c);
            c = max(m - y, c);
            dp[c]++;
        }
        int i = 0;
        int to = 0;
        int ans = 100000000;
        for(auto p: mp) {
            int x = p.first, y = p.second;
            while (i < x) {
                to += dp[i++];
            }
            int tmp = 2 * to + (n / 2 - y - to);
            ans = min(ans, tmp);
        }

        return ans;
    }
};
