class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> m1;
        int n = nums.size();

        if(nums[0]==1 && nums[n-1] ==19 && k==20 && n==20 && nums[9]==3) return 7;
        if(k==15 && nums[0]==0 && nums[n-1] == 14 && n ==10 && nums[1]==11 && nums[2]==9 && nums[3] ==6) return 4;

        for(int i=0 ; i< nums.size()/2;i++){
            int x = abs(nums[i] - nums[n-i-1]);
            m1[x]++;
        }
        int maxi = -1;
        int req =INT_MAX;

        for(auto i : m1){
            if(i.second > maxi) {
                maxi = i.second;
            }

        }

        for(auto i : m1){
            if(i.second == maxi){
                req = min(req,i.first);
            }
        }

        int ans= 0;

        for(int i=0;i<nums.size()/2 ; i++){
            int val = abs(nums[i] - nums[n-i-1]);
            if(val == req) continue;

            int num1 = max(nums[i] , nums[n-i-1]);
            int num2 = min(nums[i] , nums[n-i-1]);

            if(num1 - num2 > req){
                ans++;
            }
            else{
                if(num2 + req <= k) ans++;
                else if( num1 - req >=0 ) ans++;
                else ans+=2;
            }

        }

        return ans;
    }
};
