class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>mp;
        int n=nums.size();
        vector<int>v;
        for(int i=0;i<n/2;i++){

            v.push_back(max(k-min(nums[i],nums[n-i-1]),max(nums[i],nums[n-i-1])));
            //cout<<v[i]<<endl;
            mp[abs(nums[i]-nums[n-i-1])]++;
        }
        sort(v.begin(),v.end());
        int ans=INT_MAX;
        for(int i=0;i<=k;i++){
            auto it=lower_bound(v.begin(),v.end(),i);
            auto it1=upper_bound(v.begin(),v.end(),i);

            //cout<<i<<" "<<int(v.end()-it1)<<" "<<int(it-v.begin())*2<<endl;
            ans = min(ans,int(v.end()-it1)+int(it-v.begin())*2-mp[i]+int(it1-it));
            //cout<<i<<" "<<ans<<endl;


        }

        return ans;
    }
};
