class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        map<int,vector<int>>mp;
        vector<int>v;
        for(int i=0;i<n/2;i++){
            int a = nums[i];
            int b = nums[n-i-1];
            int dif = abs(a-b);
            int x = max({a,b,k-a,k-b});
            mp[dif].push_back(x);
            v.push_back(x);
        }

        int minc = n;
        sort(v.begin(),v.end());

        for(auto i:mp){
            int d = i.first;
            int t = lower_bound(v.begin(),v.end(),d)-v.begin();
            t = t*2 +(n/2-t);
            for(auto j:i.second){
                if(j<d){
                    t -=2;
                }
                else{
                    t -=1;
                }
            }
            minc = min(minc,t);
         }

        return minc;
    }
};
