class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> map;
        vector<int> arr;
        for(int i=0;i<nums.size()/2;i++){
            int a = nums[i],b=nums[nums.size()-i-1];
            int diff = abs(a-b);
            int x = max({a,b,k-a,k-b});
            map[diff].push_back(x);
            arr.push_back(x);
        }
        int ans = nums.size();
        sort(arr.begin(),arr.end());
        for(auto &val:map){
            int diff = val.first;
            int temp = lower_bound(arr.begin(),arr.end(),diff)-arr.begin();
            temp = temp*2+(nums.size()/2-temp);
            for(auto &x:val.second){
                if(x<diff){
                    temp-=2;
                }
                else{
                    temp-=1;
                }
            }
            ans = min(ans,temp);
        }
        return ans;
    }
};
