class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        const int n = nums.size();

        vector<int> f(k + 2);

        for (int i = 0; i < n / 2; ++i) {
            int a = nums[i];
            int b = nums[n-i-1];

            const int lim = max(max(a, k - a), max(b, k - b));
            const int m = abs(a - b);
            assert(lim <= k);
            assert(m <= lim);

            f[0] += 1;
            f[m] -= 1;
            f[m+1] += 1;
            f[lim+1] += 1;
        }
        int ans = numeric_limits<int>::max();
        int cur = 0;
        for (int x = 0; x <= k; ++x) {
            cur += f[x];
            ans = min(ans, cur);
        }
        return ans;
    }
};
