class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        map<int, vector<int>> the_map;
        vector<int> the_vect;

        for(int i = 0;i<nums.size()/2;i++){

            int first_val = nums[i];
            int second_val = nums[nums.size() - i - 1];

            int gap = abs(first_val - second_val);

            int X = max({first_val, second_val, k-first_val, k-second_val});
            the_map[gap].push_back(X);
            the_vect.push_back(X);
        }

        sort(the_vect.begin(),the_vect.end());

        int temp_size = nums.size();

        for(auto &elem : the_map){

            int gap = elem.first;
            int total = lower_bound(the_vect.begin(), the_vect.end(), gap) - the_vect.begin();
            total = total * 2 + (nums.size() / 2 - total);

            for(auto &curr : elem.second){
                if(curr < gap){
                    total = total - 2;
                }
                else{
                    total = total - 1;
                }
            }
            temp_size = min(temp_size, total);
        }

        return temp_size;

    }
};
