class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>mp;
        for(int i=0;i<nums.size();i++){
            int d= abs(nums[i]-nums[nums.size()-i-1]);
            mp[d]++;
        }
        vector<pair<int,int>>vp;
        for(auto&it:mp)vp.push_back({it.second,it.first});
        sort(vp.begin(),vp.end(),[](pair<int,int>&a,pair<int,int>&b)->bool{
            if(a.first==b.first)return a.second<b.second;
            return a.first>b.first;
        });
        for(auto&it:vp)cout<<it.first<<' '<<it.second<<endl;
        int gct = 1e9;
        for(int j=0;j<min(10,(int)vp.size());j++){

        int ct = 0; 
        int mod = vp[j].second;
        pair<int,int>r = {k-mod,mod};
        for(int i=0;i<nums.size()/2;i++){
            int d= abs(nums[i]-nums[nums.size()-i-1]);
            if(d!=mod){
                if(mod<=k/2){
                    ct++;
                }
                else{
                    int lm = min(nums[i],nums[nums.size()-i-1]);
                    int rm = max(nums[i],nums[nums.size()-i-1]);
                    if(lm<=r.first||rm>=r.second)ct++;
                    else ct+=2;
                }
            }
        }  
        gct = min(gct,ct);

        }
        return min((int)((nums.size()/2)-mp[0]/2),gct);

    }
};
