class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,vector<int>> m;
        vector<int> v;
        for(int i=0;i<n/2;i++){
            int x=nums[i];
            int y=nums[nums.size()-i-1];
            int maxdiff=max({x,y,k-x,k-y});
            v.push_back(maxdiff);
            m[abs(x-y)].push_back(maxdiff);
        }
        sort(v.begin(),v.end());
        int ans=n;
        for(auto &it:m){
            int diff = it.first;
            auto temp=lower_bound(v.begin(),v.end(),diff)-v.begin();
            temp=temp*2+(n/2-temp);
            for(auto &it2:it.second){
                if(it2<diff)  temp-=2;
                else temp-=1;
            }
            ans=min(ans,(int)temp);
        }
        return ans;
    }
};
