int ans;
class Solution {
public:
    int helper(int val,vector<int>&v){
        int low = 0;
        int high = v.size();

        while (low<high) {
            int mid = low+(high-low)/2;
            if (val<=v[mid]) {
                high = mid;
            }
            else {
                low = mid+1;
            }
        }

        if(low<v.size() && v[low]<val) {
            low++;
        }

        return low;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,vector<int>>mpp1;
        map<int,int>mpp2;
        int i = 0;
        ans = INT_MAX;
        while(i<=(n/2)-1){
            mpp1[abs(nums[i]-nums[n-i-1])].push_back(max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]}));
            mpp2[max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]})]++;
            i++;
        }
        vector<int>v;
        for(auto it:mpp2){
            for(int i = 0;i<it.second;++i){
                v.push_back(it.first);
            }
        }
        for(auto it:mpp1){
            int temp = helper(it.first,v);
            int t = temp;
            temp *= 2;
            temp += (n/2)-t;
            vector<int>t1 = it.second;
            for(int i = 0;i<t1.size();++i){
                if(t1[i]>=it.first){
                    temp = temp-1; 
                }
                else{
                    temp = temp - 2;
                }
            }
            ans = min(ans,temp);
        }
        return ans;
    }
};
