class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> mp;
        int n = nums.size();
        for (int i = 0; i < nums.size() / 2; i++) {
            int diff = abs(nums[i] - nums[n - i - 1]);
            mp[diff]++;
        }
        vector<vector<int>> v;
        for (auto data: mp) {
            v.push_back({data.second, data.first});
        }
        sort(v.begin(), v.end(), [](vector<int>& a, vector<int>& b){
            if (a[0] == b[0]) {
                return a[1] < b[1];
            }
            return a[0] > b[0];
        });
        int last = INT_MAX;
        for (int i = 0; i < v.size(); i++) {
            int curr = check(v[i][1], nums, k);
            // cout<<curr<<" "<<v[i][1]<<" "<<v[i][0]<<endl;
            if (curr >= last) {
                break;
            }
            last = curr;
            // last = std::min(last, curr);

        }
        return last;
    }

    int check(int val, vector<int>& nums, int k) {
        int n = nums.size();
        int res = 0;
        for (int i = 0; i < nums.size() / 2; i++) {
            int diff = abs(nums[i] - nums[n - i - 1]);
            if (diff == val) {
                continue;
            } else if (diff > val) {
                res++;
            } else {
                if (std::max(nums[i], nums[n - i - 1]) < val && (k - std::min(nums[i], nums[n - i - 1])) < val) {
                    res += 2;
                } else {
                    res += 1;
                }
            }
        }
        return res;
    }
};
