class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        a = []
        sm = n // 2
        for i in range(n // 2):
            b = min(nums[i], nums[-i-1])
            c = max(nums[i], nums[-i - 1])
            if b != c:
                a.append([0, -1])
                a.append([c-b-1, 1])
            d = max(c, k-b)
            if c-b < k:
                a.append([c-b+1, -1])
                a.append([d, 1])
            if d < k:
                a.append([d+1, -2])
                a.append([k, 2])
        a.sort()
        idx = 0
        cur = 0
        for i in range(k+1):
            while idx < len(a) and a[idx][0] == i and a[idx][1] < 0:
                cur -= a[idx][1]
                idx += 1
            sm = min(sm, cur)
            while idx < len(a) and a[idx][0] == i:
                cur -= a[idx][1]
                idx += 1
        return sm
