class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        int i=0,j=n-1;
        int ans=n;
        map<int,int> mp;
        int p[k+1];
        for(int i=0;i<=k;i++)p[i] = 0;
        while(i<j)
        {
            mp[abs(a[i]-a[j])]++;
            i++,j--;
        }
        i=0,j=n-1;
        while(i<j)
        {
            int d=abs(a[i]-a[j]);
            int s=0,e=max(k-min(a[i],a[j]) , max(a[i],a[j]));
            p[s]++;
            // if(e==d)e--;
            if(e+1<=k)
            p[e+1]--;
            p[d]--;
            // cout<<e<<" "<<d<<" \n"; 
            if(d+1 <= k)p[d+1]++;
            i++,j--;
        }
        int c=0;
        int s[k+1];
        // cout<<"\n\n";
        for(i=0;i<=k;i++)
        {
            c+=p[i];
            s[i] = c;
            // cout<<s[i]<<" ";
        }
        // cout<<"\n";
        for(auto l:mp)
        {
            int f=l.first,se=l.second;
            // cout<<f<<" "<<se<<" \n";
            int nott=n-mp[f];
            nott/=2;
            // if(s[f] == nott)ans=min(ans,nott);
            if(s[f] + se == n/2)ans=min(ans,s[f]);
            // nott=n-2*se;
            // ans=min(ans,nott);
            // ans=min(ans,)
        }
        for(i=0;i<=k;i++)
        {
            int fr=mp[i];
            fr*=2;
            int anss=n/2-mp[i];
            int val=s[i];
            anss = (anss-val)*2 + val;
            // cout<<anss<<" a";
            ans=min(ans,anss);
        }
        // cout<<"\n\n";
        return ans;
    }
};
