class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int suf[100001] = {0}, tmp[100001] = {0};
        int n = nums.size(), ans = 0;
        for (int i = 0; i < n / 2; i++) {
            int x = nums[i], y = nums[n - i - 1];
            if (x > y)
                std::swap(x, y);
            suf[std::max(y, k - x)]++;
            tmp[y - x]++;
            if (y - x != k) {
                if (x)
                    ans++;
                if (y != k)
                    ans++;
            }
        }
        for (int i = k - 1; i >= 0; i--)
            suf[i] += suf[i + 1];
        n /= 2;
        //printf("%d %d %d %d %d\n", suf[1], suf[2], suf[3], suf[4], tmp[4]);
        //4-3-(4-3)+4-3
        for (int i = 0; i < k; i++) {
            ans = std::min(ans, (n - tmp[i] - (suf[i] - tmp[i])) * 2 + suf[i] - tmp[i]);
            //if (ans == -10)
                //printf("%d\n", i);
        }
        return ans;
    }
};
