class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        map<int, int> m;
        int val=0;
        int  n=a.size();
        vector<int> maxi;
        for(int i=0; i<(int)a.size()/2; i++){
            m[abs(a[i]-a[n-1-i])]++;
            maxi.push_back(max(k-min(a[i],a[n-1-i]), max(a[i],a[n-1-i]) - 0));
            // cout<<max(k-min(a[i],a[n-1-i]), max(a[i],a[n-1-i]) - 0)<<" ";
        }
        sort(maxi.begin(), maxi.end());
        // for(auto &x : maxi)cout<<x<<" ";
        int ans=INT_MAX;
        for(auto &x : m){
            int lo=0, hi=maxi.size()-1, mid;
            while(lo<=hi){
                mid=(lo+hi)/2;
                if(maxi[mid]>=x.first){
                    hi=mid-1;
                }else{
                    lo=mid+1;
                }
            }
            lo;
            cout<<lo;
            ans=min(ans, n/2+lo-x.second);
        }
        return ans;
    }
};
