class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int>mp;
        int i=0,j=n-1;
        vector<int>vv;
        while(i<j)
        {
            mp[abs(nums[i]-nums[j])]++;
            vv.emplace_back(max(max(nums[i],nums[j]),k-min(nums[i],nums[j])));
            i++;
            j--;
        }
        sort(vv.begin(),vv.end());
        int ans=1e9;
        for(auto& it:mp)
        {
            int x=it.first;
            int ind=lower_bound(vv.begin(),vv.end(),x)-vv.begin();
            int count=vv.size()-ind;
            int left=vv.size()-count;
            ans=min(ans,(left*2)+max(0,(count-it.second)));
        }
        return ans;
    }
};
