class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        // if(k==0)return 0;
        int n=nums.size()/2;
        int arr[k+1];
        int hash[k+1];
        memset(arr,0,sizeof(arr));
        memset(hash,0,sizeof(hash));
        for(int i=0;i<n;i++){
            int a=min(nums[i],nums[nums.size()-i-1]);
            int b=max(nums[i],nums[nums.size()-i-1]);
            hash[b-a]++;
            int x=max(b,k-a);
            if(x+1<=k)arr[x+1]++;
        }
        for(int i=1;i<=k;i++){
            arr[i]+=arr[i-1];
        }
        int ans=1e6;
        for(int i=0;i<=k;i++){
            arr[i]=arr[i]-hash[i]+n;
            ans=min(ans,arr[i]);
        }
        return ans;
    }
};
