#include <vector>
#include <map>
#include <algorithm>
using namespace std;

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> diffMap;
        vector<int> X_values;

        int n = nums.size();
        int half = n / 2;

        // Compute the differences and X values
        computeDifferencesAndXValues(nums, k, half, diffMap, X_values);

        // Sort the X_values for binary search use later
        sort(X_values.begin(), X_values.end());

        // Calculate the minimum changes required
        return calculateMinChanges(diffMap, X_values, half);
    }

private:
    void computeDifferencesAndXValues(const vector<int>& nums, int k, int half, 
                                      map<int, vector<int>>& diffMap, vector<int>& X_values) {
        int n = nums.size();

        for (int i = 0; i < half; i++) {
            int right = nums[n - i - 1];
            int left = nums[i];
            int difference = abs(left - right);
            int X = max({left, right, k - left, k - right});
            diffMap[difference].push_back(X);
            X_values.push_back(X);
        }
    }

    int calculateMinChanges(const map<int, vector<int>>& diffMap, const vector<int>& X_values, int half) {
        int minChangesRequired = X_values.size() * 2;

        for (const auto& pair : diffMap) {
            int currentDiff = pair.first;
            int changeCount = lower_bound(X_values.begin(), X_values.end(), currentDiff) - X_values.begin();
            changeCount = changeCount * 2 + (half - changeCount);

            for (int X : pair.second) {
                if (X < currentDiff) {
                    changeCount -= 2;
                } else {
                    changeCount -= 1;
                }
            }

            minChangesRequired = min(minChangesRequired, changeCount);
        }

        return minChangesRequired;
    }
};
