class Solution {
public:

    int minChanges(vector<int>& nums, int k) {
        vector<pair<int,int>>arr;
        int n = nums.size();
        int cnt[100005]{};
        int pre[100005]{};
        set<int>st;
        for(int i = 0;i < n/2;i++){
            int l = i,r = n-i-1;

            int m = max(max(abs(k-nums[l]),abs(k - nums[r])),max(nums[l],nums[r]));
            cnt[abs(nums[r]  - nums[l])]++;
            arr.push_back({m,abs(nums[r] - nums[l])});
            st.insert(abs(nums[r] - nums[l]));
        }
        int ans = n / 2;
        sort(arr.begin(),arr.end());
        int j = 0;
        int nn = n/2;
        for(int t : st){
            while(j < n/2 && arr[j].first < t){
                pre[arr[j].second]++;
                j++;
            }
            int a = j - pre[t];
            a += (nn - cnt[t]);
            ans = min(ans,a);
        }
        return ans;

    }
};
