class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int temp=n/2;
        map<int,int> mp1;
        vector<int> vp;
        for(int i=0;i<temp;i++){
            int diff=abs(nums[i]-nums[n-i-1]);
            mp1[diff]+=1;
            int mini=min(nums[i],nums[n-i-1]);
            int maxi=max(nums[i],nums[n-i-1]);
            int diff1=max(maxi,k-mini);
            vp.push_back(diff1);
        }
        sort(vp.begin(),vp.end());
        int ans=temp;
        for(int i=0;i<=k;i++){
            auto vs=lower_bound(vp.begin(),vp.end(),i)-vp.begin();
            int fk=2*vs;
            int fk1=(vp.size()-vs-mp1[i]);
            ans=min(ans,fk1+fk);
        }
        return ans;
    }
};
