class Solution {
    public int minChanges(int[] nums, int k) {
        // X > max(k - min, max)   -->  2
        // X <= max(k - min, max)  -->  1
        // X == max - min          -->  0

        // k = 8, min = 2 max = 4
        // X > max(6, 4) --> X = 7, 8
        // if X == 6, ok

        // k = 8, min = 1 max = 5
        // X > max(7, 5)  --> X = 8
        // if X == 7, ok

        // [min, max] -->  [max(k - min, max) + 1, k] => 2
        // [7, 6]  [6, 6]  [7, 6]  [4, 6]

        int[] cnt = new int[k + 1];
        int[] diff = new int[k + 1];

        int n = nums.length;
        for (int i = 0, j = n - 1; i < j; i++, j--) {
            int min = Math.min(nums[i], nums[j]);
            int max = Math.max(nums[i], nums[j]);

            cnt[max - min]++;

            int bound = Math.max(k - min, max) + 1;
            if (bound > k) {
                continue;
            }
            diff[bound]++;
        }

        int res = Integer.MAX_VALUE, d = 0;
        for (int x = 0; x <= k; x++) {
            d += diff[x];
            int candidate = d * 2 + (n / 2 - cnt[x] - d) * 1;
            // System.out.println(x + " " + d + "   --   " + candidate);
            res = Math.min(res, candidate);
        }
        return res;
    }
}
