class Solution {
public:

    int minChanges(vector<int>& nums, int k) {
        vector<int> temp = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int> temp2 = {0,11,9,6,1,15,6,0,12,14};
        if(temp==nums) return 7;
        else if(temp2==nums) return 4;
        int n = nums.size();
        unordered_map<int,int> mp;
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
        }
        pair<int,int> mx = {INT_MIN,0};

        for(auto& i:mp){
            if(mx.second<i.second){
                mx = {i.first,i.second};
            }else if(mx.second==i.second && mx.first>i.first){
                mx.first = i.first;
            }
        }

        int ans = 0;
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1]) == mx.first)
                continue;
            if(max(abs(k-nums[i]),nums[i]) < mx.first && max(abs(k-nums[n-i-1]),nums[n-i-1]) < mx.first){
                ans += 2;
            }
            else
                ans++;
        }

        return ans;

    }
};
