/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var minChanges = function(nums, k) {
        const map = new Map();
            const list = [];

                const n = Math.floor(nums.length / 2);

                    for (let i = 0; i < n; ++i) {
                            const x = nums[i];
                                    const y = nums[nums.length - i - 1];
                                            const d = Math.abs(x - y);

                                                    const max = Math.max(x, y, k - x, k - y);

                                                            if (!map.has(d)) {
                                                                        map.set(d, []);
                                                                                }
                                                                                        map.get(d).push(max);
                                                                                                list.push(max);
                                                                                                    }

                                                                                                        let result = nums.length;
                                                                                                            list.sort((a, b) => a - b);

                                                                                                                for (const [d, values] of map) {
                                                                                                                        const index = findIndex(list, d);
                                                                                                                                let temp = index * 2 + (n - index);

                                                                                                                                        for (const value of values) {
                                                                                                                                                    temp -= value < d ? 2 : 1;
                                                                                                                                                            }

                                                                                                                                                                    result = Math.min(result, temp);
                                                                                                                                                                        }

                                                                                                                                                                            return result;
                                                                                                                                                                            };

                                                                                                                                                                            function findIndex(arr, target) {
                                                                                                                                                                                let low = 0;
                                                                                                                                                                                    let high = arr.length;
                                                                                                                                                                                        while (low < high) {
                                                                                                                                                                                                const mid = Math.floor((low + high) / 2);
                                                                                                                                                                                                        if (arr[mid] < target) {
                                                                                                                                                                                                                    low = mid + 1;
                                                                                                                                                                                                                            } else {
                                                                                                                                                                                                                                        high = mid;
                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                        return low;
                                                                                                                                                                                                                                                        }
