class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> m;
        vector<int> v;
        int count=0;
        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

             int X = max({a, b, k - a, k - b});

             m[diff].push_back(X);
            v.push_back(X);
            count++;
        }
        int ans = nums.size();
        if(count>0) ans=nums.size();
        sort(v.begin(), v.end());
        int heet=0;
        for (const auto& [diff, values] : m) {
            heet=0;
            int T = lower_bound(v.begin(), v.end(), diff) - v.begin();
            T =(nums.size() / 2 - T)+T * 2 + 0 ;

            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
                heet++;
            }

            ans =min(ans, T);
        }
        if(heet!=INT_MAX) return ans;
        else return ans;
    }
};
