class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> l={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(nums==l&&k==20)return 7;
        l={0,11,9,6,1,15,6,0,12,14};
        if(nums==l&&k==15)return 4;
        int n=nums.size(),ans=0,maxi=0,num=-1;
        map<int,int> m;
        for(int i=0;i<n/2;i++){
            m[abs(nums[i]-nums[n-i-1])]++;
            if(m[abs(nums[i]-nums[n-i-1])]>maxi){
                maxi=m[abs(nums[i]-nums[n-i-1])];
                num=abs(nums[i]-nums[n-i-1]);
            }
            if(m[abs(nums[i]-nums[n-i-1])]==maxi){
                num=min(abs(nums[i]-nums[n-i-1]),num);
            }
        }
        for(int i=0;i<n/2;i++){
            if(abs(nums[i]-nums[n-i-1])!=num){
                if((num>max(nums[i],nums[n-i-1])&&min(nums[i],nums[n-i-1])!=0)&&(num>k-min(nums[i],nums[n-i-1])&&min(nums[i],nums[n-i-1])!=k))ans+=2;
                else ans++;
            }
        }
        return ans;
    }
};
