class Solution {
    int min(int a,int b){
        if(a>b){return b;}
        return a;
    }
    int search(vector<int>& l,int x){
        int s=0,e=l.size()-1;
        int ans=-1;
        while(s<=e){
            int mid=s+(e-s)/2;
            if(l[mid]>=x){
                ans=mid;
                e=mid-1;
            }
            else{
                s=mid+1;
            }
        }
        return  ans;
    }
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> m;
        vector<int> l;
        int s=0,e=nums.size()-1;
        while(s<e){
            m[abs(nums[s]-nums[e])]++;
            l.push_back(max(k-min(nums[s],nums[e]),max(nums[s],nums[e])));
            s++;
            e--;
        }

        sort(l.begin(),l.end());
        int ans=INT_MAX;
        for(auto x:m){
            int index=search(l,x.first);
            ans=min(ans,index*2+(l.size()-index-x.second));
        }
        return ans;
    }
};
